$pagination = 0
$objTotal = @()

Do {
	$json = Invoke-RestMethod -Uri "https://squaredup-eng.atlassian.net/rest/api/3/search?jql=project = sq AND type = Bug and status not in (merged, closed) order by created desc&startAt=$pagination&maxResults=50" -Headers $Headers
    $bugCount = $json.total
    foreach ($issue in $json.issues) {
    	$obj = New-Object -TypeName psobject
        $obj | Add-Member -MemberType NoteProperty -Name key -Value $issue.key
        foreach ($field in $issue.fields) {
        	if ($null -eq $field.customfield_10031.value) {$severity='NotSet'}
			else { $severity = $field.customfield_10031.value }
		$obj | Add-Member -MemberType NoteProperty -Name severity -Value $severity
    	}
    	$objTotal += $obj
	}
	$pagination += 50
} While ($bugCount -gt $pagination)

$severity = [Ordered]@{}
$severity.add('S1', 'Critical')
$severity.add('S2', 'High')
$severity.add('S3', 'Medium')
$severity.add('S4', 'Low')
$severity.add('NotSet', 'NotSet')
$t = $objTotal.severity | Group-Object -NoElement | Sort-Object Name
$results = @()
	foreach ($code in $severity.GetEnumerator()) {
	foreach($s in $t.GetEnumerator()){
    	if ($code.Name -eq $s.Name) {
        	$r = $s.Count
        }
    }
    if ($null -eq $r){$r=0}
    $temp = New-Object -TypeName psobject
    $temp | Add-Member -MemberType NoteProperty -Name Key -Value $code.Name
    $temp | Add-Member -MemberType NoteProperty -Name Desc -Value $code.value
    $temp | Add-Member -MemberType NoteProperty -Name Value -Value $r -Force
    $results += $temp
    }
$results

